import react,{useState} from "react";

const Login=()=>{
const[username,setUserName]=useState("");
const[email,setEmail]=useState("");
const[password,setPassword]=useState("");
const[phone,setPhone]=useState("");
const[emailErr,setemailErr]=useState("");
const handleChange=(e)=>{
const name=e.target.name;
const val=e.target.value;
   if(name==="username"){
      setUserName(val);}
    else if (name==="email") {
      setEmail(val);
      setemailErr("");}
    else if (name==="password") {
      setPassword(val);}
      else if (name==="phone") {
        setPhone(val);}
      }


const handleSubmit=(e)=>{
e.preventDefault();
if (email==="") {
  setemailErr("enter your E-mail");
}
}


return(

<form onSubmit={handleSubmit}>
<span>sign in</span>
<div className="child1">
<div className="text-b">Username</div>
<input type="text" name="username" onChange={handleChange} value={username}/>
</div>
<div className="child1">
<div className="text-b">Email</div>
<input  type="email" name="email" onChange={handleChange} value={email}/>
{emailErr&&<span>{emailErr}</span>}
</div>
<div className="child1">
<div className="text-b">Password</div>
<input  type="password" name="password" onChange={handleChange} value={password}/>
</div>
<div className="child1">
<div className="text-b">Phone</div>
<input  type="text" name="phone" onChange={handleChange} value={phone}/>
</div>
<div className="btn-container"><button className="form-btn">sign in</button></div>
</form>


);
}


export default Login;
